(function() {

  $(function() {
    $("#del_btn").click(function(e) {
      var img, yesno;
      e.preventDefault();
      yesno = confirm("Are you sure you want to delete this note?");
      if (yesno) {
        img = $(this).data("short");
        return $.post("/notes/" + img + "/delete", function(data) {
          if (data[0]["success"]) {
            return window.location.href = "/notes";
          }
        });
      }
    });
    return $("#note_tags").pillbox({
      url: "/notes/json/tags",
      name: "note"
    });
  });

}).call(this);
